BEGIN TRANSACTION;

INSERT INTO core_publication
  (publication_type, pmid, authors, title, journal, publication_date, url,
   contains_promoter_data, contains_expression_data, submission_notes, curation_complete,
   reported_TF, reported_species)
SELECT
  'ARTICLE',
  '37907733',
  'deCarvalho T, Mascolo E, Caruso SM, López-Pérez J, Weston-Hafer K, Shaffer C, Erill I',
  'Simultaneous entry as an adaptation to virulence in a novel satellite-helper system infecting Streptomyces species.',
  'The ISME journal',
  '2023 Dec',
  'https://doi.org/10.1038/s41396-023-01548-0',
  1,
  1,
  'Revision reason: Matching genome still in progress
prueba11',
  1,
  'TFnuevo',
  'Pseudomonas aeruginosa'
WHERE NOT EXISTS (
  SELECT 1 FROM core_publication WHERE pmid='37907733'
);

UPDATE core_publication
SET
  authors = CASE WHEN authors IS NULL OR authors='' THEN 'deCarvalho T, Mascolo E, Caruso SM, López-Pérez J, Weston-Hafer K, Shaffer C, Erill I' ELSE authors END,
  title = CASE WHEN title IS NULL OR title='' THEN 'Simultaneous entry as an adaptation to virulence in a novel satellite-helper system infecting Streptomyces species.' ELSE title END,
  journal = CASE WHEN journal IS NULL OR journal='' THEN 'The ISME journal' ELSE journal END,
  publication_date = CASE WHEN publication_date IS NULL OR publication_date='' THEN '2023 Dec' ELSE publication_date END,
  url = CASE WHEN url IS NULL OR url='' THEN 'https://doi.org/10.1038/s41396-023-01548-0' ELSE url END,
  reported_TF = CASE WHEN reported_TF IS NULL OR reported_TF='' THEN 'TFnuevo' ELSE reported_TF END,
  reported_species = CASE WHEN reported_species IS NULL OR reported_species='' THEN 'Pseudomonas aeruginosa' ELSE reported_species END,
  contains_promoter_data = 1,
  contains_expression_data = 1,
  curation_complete = 1,
  submission_notes = CASE
    WHEN submission_notes IS NULL OR submission_notes='' THEN 'Revision reason: Matching genome still in progress
prueba11'
    ELSE submission_notes
  END
WHERE pmid='37907733';

INSERT INTO core_tffamily (name, description)
SELECT 'FMnueva', 'prueba de familia nueva'
WHERE NOT EXISTS (
  SELECT 1 FROM core_tffamily WHERE lower(name)=lower('FMnueva')
);

INSERT INTO core_tf (name, family_id, description)
SELECT 'TFnuevo', (SELECT tf_family_id FROM core_tffamily WHERE lower(name)=lower('FMnueva') LIMIT 1), 'prueba de tf nuevo'
WHERE NOT EXISTS (
  SELECT 1 FROM core_tf WHERE lower(name)=lower('TFnuevo')
);

UPDATE core_tf
SET
  family_id = COALESCE(family_id, (SELECT tf_family_id FROM core_tffamily WHERE lower(name)=lower('FMnueva') LIMIT 1)),
  description = CASE WHEN description IS NULL THEN 'prueba de tf nuevo' ELSE description END
WHERE lower(name)=lower('TFnuevo');

INSERT INTO core_tfinstance (refseq_accession, uniprot_accession, description, TF_id, notes)
SELECT
  'WP_002858684',
  'Q0PBI7',
  'peroxide stress regulator [Campylobacter jejuni subsp. jejuni NCTC 11168 = ATCC 700819].',
  (SELECT TF_id FROM core_tf WHERE lower(name)=lower('TFnuevo') LIMIT 1),
  ''
WHERE NOT EXISTS (
  SELECT 1 FROM core_tfinstance WHERE uniprot_accession='Q0PBI7'
);

UPDATE core_tfinstance
SET
  TF_id = COALESCE(TF_id, (SELECT TF_id FROM core_tf WHERE lower(name)=lower('TFnuevo') LIMIT 1)),
  refseq_accession = COALESCE(NULLIF(refseq_accession,''), 'WP_002858684'),
  description = COALESCE(NULLIF(description,''), 'peroxide stress regulator [Campylobacter jejuni subsp. jejuni NCTC 11168 = ATCC 700819].'),
  notes = COALESCE(notes, '')
WHERE uniprot_accession='Q0PBI7';

