UPDATE core_tfinstance
SET
  TF_id = (SELECT TF_id FROM core_tf WHERE lower(name)=lower('pepe') LIMIT 1),
  refseq_accession = COALESCE(NULLIF(refseq_accession,''), 'WP_003095216'),
